#!/bin/sh
basedir=$(dirname "$(echo "$0" | sed -e 's,\\,/,g')")

case `uname` in
    *CYGWIN*|*MINGW*|*MSYS*) basedir=`cygpath -w "$basedir"`;;
esac

if [ -x "$basedir/bash" ]; then
  "$basedir/bash"  "$basedir/../lcov/get_version.sh" "$@"
  ret=$?
else 
  bash  "$basedir/../lcov/get_version.sh" "$@"
  ret=$?
fi
exit $ret
